import { Trans } from '@lingui/macro'
import { validateUrlChainParam } from 'graphql/data/util'
import { ReactNode,useMemo } from 'react'
import { AlertTriangle } from 'react-feather'
import { useParams } from 'react-router-dom'
import styled from 'styled-components/macro'
import { PoolData } from 'state/pools/reducer'
import { MAX_WIDTH_MEDIA_BREAKPOINT } from '../constants'
import { HeaderRow, LoadedRow, LoadingRow } from './TokenRow'
import { useAtomValue } from 'jotai/utils'
import { POOL_HIDE } from 'constants/index'
import { useActiveNetworkVersion } from 'state/application/hooks'
import {
  filterStringAtom,
  filterTimeAtom,
  sortAscendingAtom,
  sortMethodAtom,
  TokenSortMethod,
} from 'components/Pools/state'

const PAGE_SIZE=20;
const GridContainer = styled.div`
  display: flex;
  flex-direction: column;
  max-width: ${MAX_WIDTH_MEDIA_BREAKPOINT};
  background-color: ${({ theme }) => theme.backgroundSurface};
  box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.01), 0px 4px 8px rgba(0, 0, 0, 0.04), 0px 16px 24px rgba(0, 0, 0, 0.04),
    0px 24px 32px rgba(0, 0, 0, 0.01);
  margin-left: auto;
  margin-right: auto;
  border-radius: 12px;
  justify-content: center;
  align-items: center;
  border: 1px solid ${({ theme }) => theme.backgroundOutline};
`

const TokenDataContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
  height: 100%;
  width: 100%;
`

const NoTokenDisplay = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  height: 60px;
  color: ${({ theme }) => theme.textSecondary};
  font-size: 16px;
  font-weight: 500;
  align-items: center;
  padding: 0px 28px;
  gap: 8px;
`

function NoTokensState({ message }: { message: ReactNode }) {
  return (
    <GridContainer>
      <HeaderRow />
      <NoTokenDisplay>{message}</NoTokenDisplay>
    </GridContainer>
  )
}

const LoadingRows = ({ rowCount }: { rowCount: number }) => (
  <>
    {Array(rowCount)
      .fill(null)
      .map((_, index) => {
        return <LoadingRow key={index} first={index === 0} last={index === rowCount - 1} />
      })}
  </>
)

function LoadingTokenTable({ rowCount = PAGE_SIZE }: { rowCount?: number }) {
  return (
    <GridContainer>
      <HeaderRow />
      <TokenDataContainer>
        <LoadingRows rowCount={rowCount} />
      </TokenDataContainer>
    </GridContainer>
  )
}

export default function PoolTable({ poolDatas}: { poolDatas: PoolData[] }) {
  // TODO: consider moving prefetched call into app.tsx and passing it here, use a preloaded call & updated on interval every 60s
  //const chainName = validateUrlChainParam(useParams<{ chainName?: string }>().chainName)

  const [currentNetwork] = useActiveNetworkVersion()

  const page=1;

  const sortMethod = useAtomValue(sortMethodAtom)
  const sortAscending =useAtomValue(sortAscendingAtom)
  console.log({sortMethod,sortAscending})
  const sortedPools = useMemo(() => {
    return poolDatas
      ? poolDatas
          .filter((x) => !!x && !POOL_HIDE[currentNetwork.id].includes(x.address))
          .sort((a, b) => {
            if (a && b) {
              return a[sortMethod as keyof PoolData] > b[sortMethod as keyof PoolData]
                ? (sortAscending ? -1 : 1) * 1
                : (sortAscending ? -1 : 1) * -1
            } else {
              return -1
            }
          })
          .slice(PAGE_SIZE * (page - 1), page * PAGE_SIZE)
      : []
  }, [currentNetwork.id, PAGE_SIZE, page, poolDatas, sortAscending, sortMethod])
//const sortedPools=poolDatas;

console.log("sortMethodsortMethod",{sortMethod,sortAscending});

  /* loading and error state */
  if (!sortedPools) {
    return <LoadingTokenTable rowCount={PAGE_SIZE} />
  }else if (sortedPools?.length === 0) {
    return <NoTokensState message={<Trans>No Pools found</Trans>} />
  } else {
    return (
      <GridContainer>
        <HeaderRow />
        <TokenDataContainer>
          {sortedPools.map(
            (pool, index) =>
              pool && (
                <LoadedRow
                  key={pool?.address}
                  tokenListIndex={index}
                  poolData={pool}
                />
              )
          )}
        </TokenDataContainer>
      </GridContainer>
    )
  }
}
