import { configureStore } from '@reduxjs/toolkit';
import { setupListeners } from '@reduxjs/toolkit/query/react';
import multicall from 'lib/state/multicall';
import { load, save } from 'redux-localstorage-simple';
import { isTestEnv } from 'utils/env';
import geckoTerminalApi from '../services/geckoTermial';
import application from './application/reducer';
import burn from './burn/reducer';
import burnV3 from './burn/v3/reducer';
import connection from './connection/reducer';
import { updateVersion } from './global/actions';
import lists from './lists/reducer';
import logs from './logs/slice';
import mint from './mint/reducer';
import mintV3 from './mint/v3/reducer';
import pools from './pools/reducer';
import { routingApi } from './routing/slice';
import swap from './swap/reducer';
import transactions from './transactions/reducer';
import user from './user/reducer';
import wallets from './wallets/reducer';
const PERSISTED_KEYS = ['user', 'transactions', 'lists', 'pools'];
const store = configureStore({
  reducer: {
    application,
    user,
    connection,
    transactions,
    wallets,
    swap,
    mint,
    mintV3,
    burn,
    burnV3,
    pools,
    multicall: multicall.reducer,
    lists,
    logs,
    [geckoTerminalApi.reducerPath]: geckoTerminalApi.reducer,
    [routingApi.reducerPath]: routingApi.reducer
  },
  middleware: getDefaultMiddleware => getDefaultMiddleware({
    thunk: true
  }).concat(routingApi.middleware).concat(save({
    states: PERSISTED_KEYS,
    debounce: 1000
  })).concat(geckoTerminalApi.middleware),
  preloadedState: load({
    states: PERSISTED_KEYS,
    disableWarnings: isTestEnv()
  })
});
store.dispatch(updateVersion());
setupListeners(store.dispatch);
export default store;
