import { createAction } from '@reduxjs/toolkit'
import { TokenList } from '@uniswap/token-lists'
import { NetworkInfo } from 'constants/networks'

type PopupContent = {
  listUpdate: {
    listUrl: string
    oldList: TokenList
    newList: TokenList
    auto: boolean
  }
}

 enum ApplicationModal {
  WALLET,
  SETTINGS,
  MENU,
}

const updateBlockNumber = createAction<{ chainId: number; blockNumber: number }>('application/updateBlockNumber')
const setOpenModal = createAction<ApplicationModal | null>('application/setOpenModal')
const addPopup = createAction<{ key?: string; removeAfterMs?: number | null; content: PopupContent }>(
  'application/addPopup'
)
const removePopup = createAction<{ key: string }>('application/removePopup')
const updateSubgraphStatus = createAction<{
  available: boolean | null
  syncedBlock: number | undefined
  headBlock: number | undefined
}>('application/updateSubgraphStatus')
export const updateActiveNetworkVersion = createAction<{ activeNetworkVersion: NetworkInfo }>(
  'application/updateActiveNetworkVersion'
)
