  // const filterNetwork = lowercaseChainName.toUpperCase()
  // const chainId = CHAIN_NAME_TO_CHAIN_ID[filterNetwork]
  // const L2Icon = getChainInfo(chainId)?.circleLogoUrl
  // const timePeriod = useAtomValue(filterTimeAtom)
