import axios from "axios";
import { useEffect, useState } from "react";
import { HistoricalChart,chartDays,SingleCoin } from "constants/index";
import { Line } from "react-chartjs-2";
import { TimePanel} from 'components/swap/styleds'
import useSWR from 'swr'


const generateCoingeckoUrl = (
  chainPath: string,
  address: string | undefined,
  timeFrame: LiveDataTimeframeEnum | 'live',
): string => {
  const timeTo = getUnixTime(new Date())
  const timeFrom =
    timeFrame === 'live' ? timeTo - 1000 : getUnixTime(subHours(new Date(), getTimeFrameHours(timeFrame)))

  return `${COINGECKO_API_URL}/coins/${
    chainPath
  }/contract/${address}/market_chart/range?vs_currency=usd&from=${timeFrom}&to=${timeTo}`
}


export const CoinInfo = ({ coin }:{coin:any}) => {
  const [historicData, setHistoricData] = useState<any[]>()
  const [coinData, setCoinData] = useState<any>()
  console.log("name",coin);
  const [days, setDays] = useState(1);

  const currency  = 'USD';

  const address=coin? (coin.OUTPUT ? (coin.OUTPUT.isNative ? '0x0000000000000000000000000000000000001010' : coin.OUTPUT.tokenInfo.address) : (coin.INPUT ?(coin.INPUT.isNative ? '0x0000000000000000000000000000000000001010' : coin.INPUT.tokenInfo.address) : '0x0000000000000000000000000000000000001010')) : "0x0000000000000000000000000000000000001010";

  const fetchHistoricData = async () => {

    axios.get(SingleCoin(address.toLowerCase()))
      .then(res => {
        const data = res.data;
        console.log("coinData",data);
        setCoinData(data);
      })


   const { data} = await axios.get(HistoricalChart(address.toLowerCase(), days, currency));
   setHistoricData(data.prices);



  };

  console.log("address",address);

  useEffect(() => {
    setHistoricData([]);
    fetchHistoricData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [days,address]);


  return (
      <div >
        {!historicData ? 
          <>Loading Data</>
         : 
          <>

           <div
              style={{
                display: "flex",
                width: "100%",
              }}
            >
            <h3  style={{fontSize:"28px", width: "38%", }}>
            <img  style={{
                marginRight: 5
              }} src={coinData?.image?.thumb} />
            <span>{coinData?.symbol.toUpperCase()} / {coinData?.market_data?.current_price?.usd}</span></h3>

            <div  style={{
                display: "flex",
                marginTop: 40,
                justifyContent:"right",
                width: "60%",
              }}>
              {chartDays.map((day) => (
                <TimePanel
                  key={day.value}
                  onClick={() => setDays(day.value)}

                >
                  {day.label}
                </TimePanel>

              ))}
            </div> 
            </div>
            <Line
              data={{
                labels: historicData.map((coin) => {
                  let date = new Date(coin[0]);
                  let time =
                    date.getHours() > 12
                      ? `${date.getHours() - 12}:${date.getMinutes()} PM`
                      : `${date.getHours()}:${date.getMinutes()} AM`;
                  return days === 1 ? time : date.toLocaleDateString();
                }),

                datasets: [
                  {
                    data: historicData.map((coin) => coin[1]),
                    label: `Price ( Past ${days} Days ) in ${currency}`,
                    borderColor: "#4C82FB",
                  },
                ],
              }}
              options={{
                elements: {
                  point: {
                    radius: 1,
                  },
                },
              }}
            />

          </>
        }
      </div>

  );
};
